    <script>
        function getCurrentDateTime() {
            datetime = new Date();
            hours = datetime.getHours();
            minutes = datetime.getMinutes();
            ampm = hours >= 12 ? 'PM' : 'AM';
            hours = hours % 12;
            minutes = minutes < 10 ? '0'+minutes : minutes;
            date = datetime.toDateString()
            formated = `${hours}:${minutes} ${ampm}, ${date}`;
            return formated;
        }
    
        function dateTimeDisplayKeepAlive($ref) {
            $ref.innerText = getCurrentDateTime();
    
            setInterval(() => {
                $ref.innerText = getCurrentDateTime()
            }, 1000)
        }
    </script>
